function showAccessRequestMessage(d,b){var a=$("#access-request-alert-space");var c;if(d){c=$("<div/>",{"class":"alert alert-success"});c.append("<strong>Success! </strong>")}else{c=$("<div/>",{id:"","class":"alert alert-danger"});c.append("<strong>Error: </strong>")}c.append('<a href="#" class="close" data-dismiss="alert">&times;</a>');c.append(b);a.append(c)}function submitAccessRequest(){var c=$.trim($("#access-request-username").val());var a=$.trim($("#access-request-password").val());var b=$.trim($("#access-request-email").val());$.post({url:"/access",data:{username:c,password:a,email_address:b},success:function(d){d=JSON.parse(d);if(d.successful){showAccessRequestMessage(d.successful,"Andre has been notified of your request.");$("#access-request-username").val("");$("#access-request-password").val("");$("#access-request-email").val("")}else{showAccessRequestMessage(d.successful,d.message)}},error:function(){showAccessRequestMessage(false,"A server side error occurred. Please contact Andre to let him know")}})}function verifyAccessRequest(d,a,c){var b=/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;b=b.test(c);return b&&d.length>0&&a.length>0}$("#submit-access-request").click(function(){var c=$.trim($("#access-request-username").val());var a=$.trim($("#access-request-password").val());var b=$.trim($("#access-request-email").val());if(verifyAccessRequest(c,a,b)){submitAccessRequest()}else{showAccessRequestMessage(false,"Username, password or email address not provided or not valid")}});