function is_server_on(){$.ajax({url:"/status",success:function(b){b=JSON.parse(b);return b.status},failure:function(){console.log("An error occurred when attempting to get the server's status")}})}var status_element=$("#server-status");setInterval(function(){if(is_server_on()){console.log("Server is on");status_element.attr("class","server-status-on")}else{console.log("Server is off");status_element.attr("class","server-status-off")}},5000);